import React, { useState, useEffect } from 'react';
import { tracking } from './MOCK_DATA';
import Box from './Box';
import { db } from '../firebase';
import { Button, makeStyles } from '@material-ui/core';

const useStyles = makeStyles({
  button: {
    textTransform: 'none',
    boxShadow: 'none'
  }
})

function Scanned() {

  const classes = useStyles();
  const [items, setItems] = useState([]);
  const [currentIndex, setCurrentIndex] = useState();

  useEffect(() => {
    db.collection('scannedItems').onSnapshot(snapshot => {
      setItems(snapshot.docs);
    })
  }, [])

  const handleCancle = () => {
    console.log('test');
  }

  return (
    <div className='box'>
      <div className='box__header'>
        <p>Scanned</p>
        <p className='box__number'>{items.length}</p>
      </div>
      {
        items.length === 0 ? <p className='box__content' style={{ color: "rgb(100, 100, 100)" }}>No item.</p>
          : items.map((item, index) => {
            return <div className='box__content'>
              <div className='box__content__header'>
                <p className='box__tracking'>{item.data().backup.trackingNumber}</p>
                <Button onClick={handleCancle} className={classes.button} variant="contained" color='secondary'>Cancel</Button>
              </div>
              <p>{item.data().backup.status}</p>
              <div className='box__content__footer'>
                <p>{item.data().parcelType}</p>
                <p>{item.data().amount}</p>
              </div>
            </div>
          }
          )
      }
    </div>
  )
}

export default Scanned
