import React, { useState, useEffect } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import { TextField, Button } from '@material-ui/core';
import './TrackingInput.css';

const useStyles = makeStyles({
  button: {
    backgroundColor: '#1E9DF3',
    textTransform: 'none',
    border: 'none',
    boxShadow: 'none',
    color: 'white',
    height: '3rem',
    fontSize: '1.2rem',
    '&:hover': {
      backgroundColor: '#0774BD',
      boxShadow: 'none',
    },
    '&:disabled': {
      backgroundColor: "#1E9DF3",
      color: 'white',
      opacity: 0.7
    }
  },

  textField: {
    marginBottom: '1.5rem',
    '&:hover': {
      outlined: 'none'
    }
  }

});

function TrackingInput() {

  const classes = useStyles();

  const [trackingNumber, setTrackingNumber] = useState('');
  const [parcelType, setParcelType] = useState();
  const [amount, setAmount] = useState();
  const [disabled, setDisabled] = useState(false);


  useEffect(() => {

  }, [trackingNumber])


  return (
    <form className='trackingform'>
      <label className='trackingform__label'>Tracking Number</label>
      <TextField
        value={trackingNumber}
        onChange={(e) => setTrackingNumber(e.target.value)}
        className={classes.textField}
        placeholder='xxxx-xxxx-xxxx'
        margin="dense"
        variant="outlined"
        type='text'
        maxLength='2'
        inputProps={{ maxLength: 12 }}
      />
      <label className='trackingform__label'>Parcel Type</label>
      <TextField
        className={classes.textField}
        margin="dense"
        variant="outlined"
      />
      <label className='trackingform__label'>Amount</label>
      <TextField
        className={classes.textField}
        id="outlined-margin-dense"
        defaultValue={1}
        margin="dense"
        variant="outlined"
        type='text'

      />
      <Button disabled={false} className={classes.button} variant='contained'>{'Add To Scanned List -->'}</Button>
    </form>
  )
}

export default TrackingInput
