import React, { useEffect, useState } from 'react';
import './Box.css';

function Box({ item }) {

  const now = new Date();
  const [remaining, setRemaining] = useState('');

  useEffect(() => {

    const now = new Date();
    const date = new Date(item.date);
    // console.log("Date : ", date);
    // console.log("Now : ", now);
    const second = 1000;
    const minute = 60 * second;
    const hour = 60 * minute;
    const day = 24 * hour;
    const distance = date - now;

    if (distance > 0) {
      const remainingDay = Math.floor(distance / day);
      const remainingHour = Math.floor((distance % day) / hour);
      if (remainingDay > 0 && remainingDay == 1) {
        setRemaining(`${remainingDay} day`)
      } else if (remainingDay > 0 && remainingDay > 1) {
        setRemaining(`${remainingDay} days`)
      } else {
        if (remainingHour > 1) {
          setRemaining(`${remainingHour} hours`)
        } else {
          setRemaining(`${remainingHour} hour`)
        }
      }
    }
  }, [])

  return (
    <div className='box__content'>
      <div className='box__content__header'>
        <p className='box__tracking'>{item.trackingNumber}</p>
        <p className='box__smalldetails'>{item.date}</p>
      </div>
      <p>{item.address}</p>
      <p className='box__smalldetails box__status'>{item.status}</p>
    </div>
  )
}

export default Box
